// --2. Componente Base ("Símbolo") 
// Encapsula la lógica de hover, tamaño y el fondo animado.
// - children: Los paths del SVG específicos
// - viewBox: Las coordenadas orginales del svg
// - className: Para sobreescribir tamaño (ej: "w-16 h-16") o color.
// - initialColor: "fill-white" (para navbar) o "fill-current" (para heredar color de texto)
// Actualizado para soportar 'href' para SVG Sprites externos
const LogoBackBase = ({ viewBox, children, href, className = "w-10 h-10", initialColor = "fill-neutral-900" }) => {
  // Clase común para la animación de color
  const pathClass = `${initialColor} group-hover:fill-[url(#logoTornasolSharedGreen)] transition-all duration-500`;

  return (
    <div className={`group relative flex items-center justify-center overflow-hidden cursor-pointer ${className}`}>
      {/* Capa de Fondo Tornasol: Se mueve lateralmente al hacer hover */}
      <div className={`absolute inset-0 ${tornasolGradientGreen} transition-transform duration-500 ease-in-out group-hover:-translate-x-full`}></div>

      {/* SVG Container */}
      <svg 
        viewBox={viewBox} 
        className="relative z-10 w-full h-full p-1.5 transition-all duration-500"
        xmlns="http://www.w3.org/2000/svg"
      >
        <SharedGradientDefinitions />
        
        {/* LÓGICA HÍBRIDA: */}
        {href ? (
          // Opción A: Usando Sprite externo (<use>)
          <use href={href} className={pathClass} />
        ) : (
          // Opción B: Usando SVG Inline (Children)
          React.Children.map(children, child => {
            if (React.isValidElement(child)) {
              return React.cloneElement(child, {
                className: `${child.props.className || ''} ${pathClass}`
              });
            }
            return child;
          })
        )}
      </svg>
    </div>
  );
};

